package p1110;

public class SwitchStringCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "ccc";
		switch(name) {
		case "aaa":
			System.out.println("name이 aaa");
			break;
		case "bbb":
			System.out.println("name이 bbb");
			break;
		case "ccc":
			System.out.println("name이 ccc");
			break;
		default:
			System.out.println("name이 아무개");
			break;
		}
	}

}
